import { Component, ViewChild } from '@angular/core';
import { NavController } from 'ionic-angular';
import { SignaturePad } from 'angular2-signaturepad/signature-pad';
import { ToastController } from 'ionic-angular';
import { Platform } from 'ionic-angular';

function getFileFromServer(time, url, doneCallback) {
    var xhr;

    xhr = new XMLHttpRequest();
    xhr.onreadystatechange = handleStateChange;
    xhr.open("GET", url, true);
      // Set headers to force the form to be reloaded every time
    xhr.setRequestHeader('Cache-control', 'no-cache');
    xhr.setRequestHeader('Cache-control', 'no-store');
    xhr.setRequestHeader('Expires', '0');
    xhr.setRequestHeader('Pragma', 'no-cache');

    setTimeout(()=>{xhr.send();},time);

    function handleStateChange() {
        if (xhr.readyState === 4) {
            doneCallback(xhr.status == 200 ? xhr.responseText : null);
        }
    }
}

function putFileOnServer(url, data, doneCallback) {
    var xhr;
    xhr = new XMLHttpRequest();
    xhr.onreadystatechange = handleStateChange;
    xhr.open("POST", url, true);
    xhr.send(data);

    function handleStateChange() {
        if (xhr.readyState === 4) {
            doneCallback(xhr.status == 200 ? xhr.responseText : null);
        }
    }
}

@Component({
  selector: 'page-home',
  templateUrl: 'home.html'
})

export class HomePage {
  testFlag = false;
  version = 'v1.7a';
  signature = '';
  formLoaded = false;
  infoText = "Unten tippen, um das Formular zu laden";
  signStarted = false;
  formAsImg = null;
  height = 0;
  width = 0;
  config = {
    'serverPathLocal' : '/sigma/testOrdi',
    'serverPath' : 'https://www.ordination-kutschera.at/sigma/testOrdi'
  }
  formURL = '';
  sigURL = '';

  @ViewChild(SignaturePad) signaturePad: SignaturePad;
  public signaturePadOptions: Object = { // Check out https://github.com/szimek/signature_pad
    'minWidth': 0.7,
    'maxWidth': 1.5,
    'minDistance': 0,
    'canvasWidth': 800,
    'canvasHeight': 1120,
    'backgroundColor': '#ffffff',
    'penColor': '#666a73'
  };

  constructor(public navController: NavController, public toastCtrl: ToastController, public plt: Platform) {}

  ionViewDidEnter() {
    this.formLoaded = false;

    //---check device size---
    this.width = this.plt.width();
    this.height = this.plt.height();
      //--check, whether width or height is "longest side"--
    if (this.width * 1120 / 800 > this.height) {
      this.width = this.height * 800 / 1120
    }
    else {
      this.height = this.width * 1120 / 800
    }

    this.signaturePad.set("canvasWidth",this.width);
    this.signaturePad.set("canvasHeight",this.height);

    this.signaturePad.clear()

    //---check for local development---
    this.formURL = (this.testFlag ? this.config.serverPathLocal : this.config.serverPath) + "/form.txt";
    this.sigURL = (this.testFlag ? this.config.serverPathLocal : this.config.serverPath) + "/saveImg.php";
}

  drawComplete() {
    //--- if no form loaded, drawing serves as reload---
    if(!this.formLoaded) {

      //--- Animation for loading
      this.infoText = "Wird geladen...";
      this.drawImg(1);
      let time = 245;
      setTimeout(()=>{ this.drawImg(2)},time*1);
      setTimeout(()=>{ this.drawImg(3)},time*2);
      setTimeout(()=>{ this.drawImg(4)},time*3);
      setTimeout(()=>{ this.drawImg(1)},time*4);
      setTimeout(()=>{ this.drawImg(2)},time*5);
      setTimeout(()=>{ this.drawImg(3)},time*6);
      setTimeout(()=>{ this.drawImg(4)},time*7);
      setTimeout(()=>{ this.drawImg(1)},time*8)

      getFileFromServer(2000, this.formURL, (text) => {
          if (text === null) {
              // alert("Error");
          }
          else {
            this.formAsImg = "data:image/png;base64," + text;
            this.signaturePad.fromDataURL(this.formAsImg, {width: this.width, height: this.height});
            this.formLoaded = true;
            this.infoText = "Nun können Sie signieren";
          }
      });
    }
    //---if form was loaded, drawing activates the buttons for sending/reloading---
    else if (!this.signStarted) {
      this.signStarted = true;
    }
  }

  savePad() {
    //--- save signature and clear form ---
    this.signature = this.signaturePad.toDataURL();
    this.infoText = "";
    this.signaturePad.clear();
    this.formLoaded = false;
    this.signStarted = false;
    this.formAsImg = null;

    //--- upload on server ---
    let data = new FormData();
    data.append('type', 'signature');
    data.append('image', this.signature);

    putFileOnServer(this.sigURL,data, (text) => {
      this.infoText = "Signatur gespeichert!";
      setTimeout(()=>{this.infoText = "Unten tippen, um das Formular zu laden"}, 3000);
    });

    // let toast = this.toastCtrl.create({
    //   message: 'Neue Signatur gespeichert',
    //   duration: 3000
    // });
    // toast.present();
  }

  clearPad() {
    this.signaturePad.clear();
    this.signStarted = false;

    //--- Animation for loading
    this.infoText = "Wird geladen...";
    this.drawImg(1);
    let time = 245;
    setTimeout(()=>{ this.drawImg(2)},time*1);
    setTimeout(()=>{ this.drawImg(3)},time*2);
    setTimeout(()=>{ this.drawImg(4)},time*3);
    setTimeout(()=>{ this.drawImg(1)},time*4);

    getFileFromServer(1000, this.formURL, (text) => {

        if (text === null) {
            // alert("Error");
        }
        else {
          this.formAsImg = "data:image/png;base64," + text;
          this.signaturePad.fromDataURL(this.formAsImg, {width: this.width, height: this.height});
          this.formLoaded = true;
          this.infoText = "Nun können Sie signieren";
        }
    });

    // this.signStarted = false;
    // //--- if form has been downloaded, draw it again --
    // if(this.formAsImg) {
    //   this.signaturePad.fromDataURL(this.formAsImg, {width: 800, height: 1120});
    // }
  }
  drawImg(n){
    this.signaturePad.fromDataURL(this.returnImg(n), {width: this.width, height: this.height});
  }
  returnImg(n){
    switch(n){
      case 1: return "data:image/png;base64,"
      case 2: return "data:image/png;base64,";
      case 3: return "data:image/png;base64,";
      case 4: return "data:image/png;base64,";
    }
  }
}
